var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.philamfood.com/eskinol-classic-facial-cleanser-with-grains-7.6oz.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Eskinol Classic Facial Cleanser With Grains 7.6oz",
                    "price": "4.29",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://philam-philamfood1.netdna-ssl.com/images/P/4800003439557.jpg",
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.philamfood.com/belo-essentials-moisture-rich-whitening-toner-100ml.html"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Belo Essentials Moisture-Rich Whitening Toner 100ml",
                    "price": null,
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://philam-philamfood1.netdna-ssl.com/images/P/4806518330280.jpg",
                    country: 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.philamfood.com/facial/"
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Eskinol Pimple Fighting Face Wash 100g';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '7.69',
                    url: 'https://www.philamfood.com/eskinol-pimple-fighting-face-wash-100g.html',
                    image: 'https://philam-philamfood1.netdna-ssl.com/images/T/eskinol%20pimple%20face%20wash%20100g_tn.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Gluta C Facial Day Cream Whitening Anti Aging Spf 25 30ml';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '11.99',
                    url: 'https://www.philamfood.com/gluta-c-facial-day-cream-whitening-anti-aging-spf-25-30ml.html',
                    image: 'https://philam-philamfood1.netdna-ssl.com/images/T/ardentglutacday_tn.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.philamfood.com/facial/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 36,
                    "pages": 3
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.philamfood.com/facial/"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Facial"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
