    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.physioroom.com/catalog/Sports_Knee_Supports/3111_0_alpha.html#products"
        }

        // Change to be the number of products on the page
        var x = 43;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Donjoy Armor with Fourcepoint Knee Brace';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '299.99',
                    url: 'http://www.physioroom.com/product/Donjoy_Armor_with_Fourcepoint_Knee_Brace/3111/38207.html',
                    image: 'http://www.physioroom.com/images/products/38207_image.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Vulkan Elasticated Knee Support';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '299.99',
                    url: 'http://www.physioroom.com/product/Vulkan_Elasticated_Knee_Support/3111/40660.html',
                    image: 'http://www.physioroom.com/images/products/40660_image.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

