    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.pier1.com/dining-room-sets#nav=left",
            "html": cache.load('https://www.pier1.com/dining-room-sets#nav=left')
        }

        // Change to be the number of products on the page
        var x = 52;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Natural Stonewash 7-Piece Dining Set';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1599.89',
                    url: 'https://www.pier1.com/bradding-natural-stonewash-7-piece-dining-set/IDT100.html?cgid=dining-room-sets',
                    image: 'https://www.pier1.com/dis/dw/image/v2/AAID_PRD/on/demandware.static/-/Sites-pier1_master/default/dwfc9c49c7/images/IDT100/IDT100_1.jpg?sw=225&sh=225',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Build Your Own Mahogany Brown Dining Collection';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: null,
                    url: 'https://www.pier1.com/build-your-own-torrance-mahogany-brown-dining-collection/IDT004.html?cgid=dining-room-sets',
                    image: 'https://www.pier1.com/dis/dw/image/v2/AAID_PRD/on/demandware.static/-/Sites-pier1_master/default/dw8c879af4/images/IDT004/IDT004_1.jpg?sw=225&sh=225',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.pier1.com/dining-room-sets#nav=left",
            "html": cache.load('https://www.pier1.com/dining-room-sets#nav=left')
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Dining Room Sets",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


