var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.potterybarn.com/products/benchwright-coffee-table/?pkey=ccoffee-tables%7C4294347858&isx=0.0.1060#",
            "html": cache.load("https://www.potterybarn.com/products/benchwright-coffee-table/?pkey=ccoffee-tables%7C4294347858&isx=0.0.1060#")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Benchwright Rectangular Coffee Table",
                    "price": "999.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.potterybarn.com/pbimgs/rk/images/dp/wcm/201826/0105/benchwright-rectangular-coffee-table-rustic-mahogany-c.jpg",
                    "country": 'us',
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.potterybarn.com/shop/gifts/electronic-gifts/?cm_type=gnav",
            "html": cache.load("https://www.potterybarn.com/shop/gifts/electronic-gifts/?cm_type=gnav")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Tech & Gadgets",
                    "url":"https://www.potterybarn.com/shop/gifts/electronic-gifts/?cm_type=gnav"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "https://www.potterybarn.com/shop/gifts/electronic-gifts/?cm_type=gnav",
                    "html": cache.load("https://www.potterybarn.com/shop/gifts/electronic-gifts/?cm_type=gnav")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.potterybarn.com/shop/gifts/electronic-gifts/?cm_type=gnav",
            "html": cache.load("https://www.potterybarn.com/shop/gifts/electronic-gifts/?cm_type=gnav")
        }

        // Change to be the number of products on the page
        var x = 61;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Smart Phone Instalens On The Go Photo Kit';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '19.50',
                    url: 'https://www.potterybarn.com/products/smart-phone-instalens-on-the-go-photo-kit/?pkey=celectronic-gifts&isx=0.0',
                    image: 'https://www.potterybarn.com/pbimgs/rk/images/dp/wcm/201832/0083/smart-phone-instalens-on-the-go-photo-kit-j.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })
        })

        var lastProductName = 'SONOS PLAYBAR Speaker';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '699.00',
                    url: 'https://www.potterybarn.com/products/sonos-playbar/?pkey=celectronic-gifts&isx=0.0',
                    image: 'https://www.potterybarn.com/pbimgs/rk/images/dp/wcm/201821/0005/sonos-playbar-speaker-j.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


