var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.powertoolworld.co.uk/accessories/air-tool-accessories/trend-wp-air-p-16-headband-fixing-kit-1-pr-air-pro",
            "html": cache.load("https://www.powertoolworld.co.uk/accessories/air-tool-accessories/trend-wp-air-p-16-headband-fixing-kit-1-pr-air-pro")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Trend WP-AIR/P/16 Headband fixing kit 1 pr AIR/PRO Trend WP-AIR/P/16 Headband fixing kit 1 pr AIR/PRO",
                    "price": "13.29",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://skin.powertoolworld.co.uk/skin/frontend/powertoolworld/default/images/ptw_logo.png",
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "",
            "html": cache.load("")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": ""
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "",
                    "html": cache.load("")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.powertoolworld.co.uk/accessories/air-tool-accessories?p=2",
            "html": cache.load("https://www.powertoolworld.co.uk/accessories/air-tool-accessories?p=2")
        }

        // Change to be the number of products on the page
        var x = 12;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Trend WP-AIR/P/05 Switch and contact assembly';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '43.78',
                    url: 'https://www.powertoolworld.co.uk/accessories/air-tool-accessories/trend-wp-air-p-05-switch-and-contact-assembly',
                    image: 'https://media.powertoolworld.co.uk/media/catalog/product/cache/1/image/202x/602f0fa2c1f0d1ba5e241f914e856ff9/w/p/wp-air-p_05.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Trend WP-AIR/03 Face seal for Airshield';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '11.45',
                    url: 'https://www.powertoolworld.co.uk/accessories/air-tool-accessories/trend-wp-air-03-face-seal-for-airshield',
                    image: 'https://media.powertoolworld.co.uk/media/catalog/product/cache/1/image/202x/602f0fa2c1f0d1ba5e241f914e856ff9/w/p/wp-air_03.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


