var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.toolstation.com', function() {

        describe('In stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.prezzybox.com/duel-foot-massager.aspx",
            "html" : cache.load("https://www.prezzybox.com/duel-foot-massager.aspx")
        }

        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)
            console.log(inStockPage)
            inStockPage.name.should.be.equal("Dual Foot Massager");
            inStockPage.price.should.be.equal("12.99");
            inStockPage.url.should.be.equal("https://www.prezzybox.com/duel-foot-massager.aspx");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('https://media.prezzybox.com/Prezzybox/Widgets/SiteWideBanners/199_Delivery_SW_v1.png');
            inStockPage.country.should.be.equal('uk');            
         });
 
    });

    xdescribe('Pre-order Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.tesco.com/direct/a-way-out-xbox-one/542-7362.prd?skuId=542-7362",
            "html" : cache.load("https://www.tesco.com/direct/a-way-out-xbox-one/542-7362.prd?skuId=542-7362")
        }

        it('should return false when product is available for pre order', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "A Way Out Xbox One",
                    "price": "22.00",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://tesco.scene7.com/is/image/tesco/542-7362_PI_66650AL6?op_sharpen=1&id=FBrbB0&fmt=jpg&fit=constrain,1&wid=400&hei=507&op_sharpen=1",
                    "country": "uk",
                    "sku": '542-7362'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.tesco.com/direct/character-minions-printed-100-cotton-beach-towel/104-9626.prd?skuId=104-9626&source=others",
            "html" : cache.load("https://www.tesco.com/direct/character-minions-printed-100-cotton-beach-towel/104-9626.prd?skuId=104-9626&source=others")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Character Minions Printed 100% Cotton Beach Towel",
                    "price": "7.40",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://tesco.scene7.com/is/image/tesco/104-9626_PI_1000540MN?op_sharpen=1&id=HzNaG0&fmt=jpg&fit=constrain,1&wid=506&hei=506&op_sharpen=1",
                    "country": "uk",
                    "sku": '107557'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.prezzybox.com/childrens-gifts.aspx",
            "html" : cache.load("https://www.prezzybox.com/childrens-gifts.aspx")
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Smartphone Gloves - Medium Black';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '4.99',
                url: 'https://www.prezzybox.com/smartphone-gloves-medium-black.aspx',
                image: 'https://cdn.prezzybox.com/Images/48137.jpg?i10c=img.resize(width:262,height:262);img.sharpen(amount:0.1)&thumbnail=true',
                country: 'uk'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Smart Wallet - Phone Wallet';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '3.99',
                url: 'https://www.prezzybox.com/smart-wallet.aspx',
                image: 'https://cdn.prezzybox.com/Images/19480.jpg?i10c=img.resize(width:262,height:262);img.sharpen(amount:0.1)&thumbnail=true',
                country: 'uk'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    });

    xdescribe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.prezzybox.com/promotion/clearance",
            "html" : cache.load("https://www.prezzybox.com/promotion/clearance")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "items": 7,
                "pages": null,
                "url":"https://www.bitsandpieces.com/category/childrens-puzzles"
            })

            const pagination = Structure(payload)

            JSON.stringify(pagination).should.equal(expected);
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.prezzybox.com/promotion/clearance",
            "html" : cache.load("https://www.prezzybox.com/promotion/clearance")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "name": "Clearance",
                "url":"https://www.bitsandpieces.com/category/childrens-puzzles"
            })

            const category = Structure(payload)

            JSON.stringify(category).should.equal(expected);
        });
    });

});


