var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.pricefalls.com/product/trevco-bm2657-adv2-16x20-batman-issue-251-quickpro-artwrap-back-board-white-16-x-20-in/321223148",
            "html": cache.load("https://www.pricefalls.com/product/trevco-bm2657-adv2-16x20-batman-issue-251-quickpro-artwrap-back-board-white-16-x-20-in/321223148")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Trevco BM2657-ADV2-16x20 Batman Issue 251-Quickpro Artwrap Back Board, White - 16 x 20 in.",
                    "price": "27.56",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://f41c117ffae182d239aa-b9adb219c950f8b5c86533900d876b85.ssl.cf2.rackcdn.com/243/321223148_1.jpg',
                    "country": 'us',
                    "sku": "321223148"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.pricefalls.com/search/home-living/home-decor/art-wall-decor/prints-posters?sort=score&vm=list&fq-category=Home+%26+Living%7CHome+Decor%7CArt+%26+Wall+Decor%7CPrints+%26+Posters&fq-brand=fathead~~trevco&vc-active=0&vc-f_search=1&vc-f_price=1&vc-f_gender=0&vc-f_product_rating_avg=1&vc-f_cats-show=1&vc-f_brand-show=1&vc-f_brand-select=0&vc-f_material-show=0&vc-f_material-select=1&vc-f_color-show=1&vc-f_color-select=1&vc-f_size-show=1&vc-f_size-select=1&vc-f_pattern-show=0&vc-f_pattern-select=1&vc-f_store_id-show=1&vc-f_store_id-select=1",
            "html": cache.load("https://www.pricefalls.com/search/home-living/home-decor/art-wall-decor/prints-posters?sort=score&vm=list&fq-category=Home+%26+Living%7CHome+Decor%7CArt+%26+Wall+Decor%7CPrints+%26+Posters&fq-brand=fathead~~trevco&vc-active=0&vc-f_search=1&vc-f_price=1&vc-f_gender=0&vc-f_product_rating_avg=1&vc-f_cats-show=1&vc-f_brand-show=1&vc-f_brand-select=0&vc-f_material-show=0&vc-f_material-select=1&vc-f_color-show=1&vc-f_color-select=1&vc-f_size-show=1&vc-f_size-select=1&vc-f_pattern-show=0&vc-f_pattern-select=1&vc-f_store_id-show=1&vc-f_store_id-select=1")
        }

        // Change to be the number of products on the page
        var x = 15;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Trevco BM2657-ADV2-16x20 Batman Issue 251-Quickpro Artwrap Back Board, White - 16 x 20 in.';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: '190860040237',
                    name: firstProductName,
                    price: '48.25',
                    url: 'https://www.pricefalls.com/product/trevco-bm2657-adv2-16x20-batman-issue-251-quickpro-artwrap-back-board-white-16-x-20-in/321223148',
                    image: 'https://cacec2d2bfd37eb2c0a7-d61c2b71b078f0f3eb1a4b02aef1da82.ssl.cf2.rackcdn.com/243/321223148_1.jpg',
                    country: 'us',
                    sku: '321223148'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Trevco BM2674-ADV2-12x18 Batman Joker Smile-Quickpro Artwrap Back Board, White - 12 x 18 in.';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: '190860040503',
                    name: lastProductName,
                    price: '42.42',
                    url: 'https://www.pricefalls.com/product/trevco-bm2674-adv2-12x18-batman-joker-smile-quickpro-artwrap-back-board-white-12-x-18-in/321221899',
                    image: 'https://cacec2d2bfd37eb2c0a7-d61c2b71b078f0f3eb1a4b02aef1da82.ssl.cf2.rackcdn.com/243/321221899_1.jpg',
                    country: 'us',
                    sku: '321221899'

                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "https://www.pricefalls.com/search?q[]=boys+costume",
            "html": cache.load("https://www.pricefalls.com/search?q[]=boys+costume")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 15,
                    "pages": 47,
                    "url": "https://www.pricefalls.com/search?q[]=boys+costume"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.pricefalls.com/search?q[]=boys+costume",
            "html": cache.load("https://www.pricefalls.com/search?q[]=boys+costume")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "boys costume",
                    "url": "https://www.pricefalls.com/search?q[]=boys+costume"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
