describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.probikekit.co.uk/components/bottom-brackets-cups-spares.list",
        "html": cache.load("https://www.probikekit.co.uk/components/bottom-brackets-cups-spares.list")
    }

    // Change to be the number of products on the page

        // Change to be the number of products on the page
        var x = 30;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Shimano BB-R60 Ultegra 6800 Bottom Bracket';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '16.99',
                url: 'https://www.probikekit.co.uk/bottom-bracket-cups-spares/shimano-bb-r60-ultegra-6800-bottom-bracket/11135260.html',
                image: 'https://s3.thcdn.com/productimg/300/300/11135260-1284303329391668.jpg',
                country: 'uk',
                sku:"11135260"
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Rotor BB30 To 24mm Bottom Bracket Converter - Ceramic';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '125.99',
                url: 'https://www.probikekit.co.uk/bottom-bracket-cups-spares/rotor-bb30-to-24mm-bottom-bracket-converter-ceramic/11529969.html',
                image: 'https://s1.thcdn.com/productimg/300/300/11529969-1384541352136902.jpg',
                country: 'uk',
                sku:"11529969"
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    });
