    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.qvc.com/for-the-home/crafts-&-scrapbooking/die-cutting-machines/_/N-1cmt1/c.html?limit=96&ro=203k1Z1cmt1&navSrc=REFINE#plModule",
            "html": cache.load("http://www.qvc.com/for-the-home/crafts-&-scrapbooking/die-cutting-machines/_/N-1cmt1/c.html?limit=96&ro=203k1Z1cmt1&navSrc=REFINE#plModule")
        }

        // Change to be the number of products on the page
        var x = 34;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Cricut Maker Cutting Machine';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '399.99',
                    url: 'http://www.qvc.com/Cricut-Maker-Cutting-Machine.product.F250620.html',
                    image: 'https://qvc.scene7.com/is/image/QVC/f/20/f250620.001?$aemprodgallery$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Silhouette Cameo3 Basics Bundle with Accessories';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '296.00',
                    url: 'http://www.qvc.com/Silhouette-Cameo3-Basics-Bundle-with-Accessories.product.F12896.html',
                    image: 'https://qvc.scene7.com/is/image/QVC/f/96/f12896.001?$aemprodgallery$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
