var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.radioshack.com/collections/radios/products/radioshack-am-fm-stereo-headset-radio-2?variant=20332572165",
            "html": cache.load("https://www.radioshack.com/collections/radios/products/radioshack-am-fm-stereo-headset-radio-2?variant=20332572165")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "AM/FM Stereo Headset Radio",
                    "price": "55.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cdn.shopify.com/s/files/1/1490/5112/products/01200518_00_b980f000-8fe3-45f2-ab9e-3bc481c5be74_450x.jpg?v=1479192029",
                    "country": 'us',
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.radioshack.com/collections/radios/products/radioshack-digital-am-fm-pocket-radio?variant=20332560645",
            "html": cache.load("https://www.radioshack.com/collections/radios/products/radioshack-digital-am-fm-pocket-radio?variant=20332560645")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Digital AM/FM Pocket Radio with Backlit Display",
                    "price": "30.00",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://cdn.shopify.com/s/files/1/1490/5112/products/01201475_00_450x.jpg?v=1522136696',
                    "country": 'us',

                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.radioshack.com/collections/radios",
            "html": cache.load("https://www.radioshack.com/collections/radios")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Radios",
                    "url": "https://www.radioshack.com/collections/radios"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "",
                    "html": cache.load("")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.radioshack.com/collections/radios",
            "html": cache.load("https://www.radioshack.com/collections/radios")
        }

        // Change to be the number of products on the page
        var x = 11;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'AM/FM Stereo Headset Radio';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '55.00',
                    url: 'http://www.radioshack.com/collections/radios/products/radioshack-am-fm-stereo-headset-radio-2?variant=20332572165',
                    image: 'https://cdn.shopify.com/s/files/1/1490/5112/products/01200518_00_b980f000-8fe3-45f2-ab9e-3bc481c5be74_200x.jpg?v=1479192029',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Mini AM/FM/WX Crank Radio';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '24.00',
                    url: 'http://www.radioshack.com/collections/radios/products/radioshack-mini-am-fm-wx-crank-radio?variant=20332486789',
                    image: 'https://cdn.shopify.com/s/files/1/1490/5112/products/02000654_00_faffc43a-7972-4d62-9a42-83ea68bf8685_200x.jpg?v=1478051777',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


