var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.rakuten.com', function () {

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.rakuten.com/prod/sony-playstation-4-500gb-gaming-console/269811047.html",
            "html": cache.load("http://www.rakuten.com/prod/sony-playstation-4-500gb-gaming-console/269811047.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Sony PlayStation 4 500GB Gaming Console",
                    "price": "375.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://img1.r10.io/PIC/76985918/0/1/450/76985918.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.rakuten.com/prod/ps4-designer-skin-for-sony-playstation-4-console-two-2-decals-for-ps4/260654704.html",
            "html": cache.load("http://www.rakuten.com/prod/ps4-designer-skin-for-sony-playstation-4-console-two-2-decals-for-ps4/260654704.html")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "PS4-Designer Skin for Sony PlayStation 4 Console & Two(2) Decals for: PS4 Dualshock Controller - Digicamo Red",
                    "price": "sold out",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://img1.r10.io/PIC/66037839/0/1/450/66037839.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.rakuten.com/category/108/",
            "html": cache.load("https://www.rakuten.com/category/108/")
        }

        // Change to be the number of products on the page
        var x = 7;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Critters Go For A Ride 100 Large Piece Jigsaw Puzzle';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '4.99',
                url: 'http://www.bitsandpieces.com/product/critters-go-for-a-ride-100-large-piece-jigsaw-puzzle-42777',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/42777.jpg',
                country: 'us'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Soccer Sliding Wooden Puzzle';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '14.99',
                url: 'http://www.bitsandpieces.com/product/soccer-sliding-wooden-puzzle',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/43952.jpg',
                country: 'us'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })
    }); 
    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.rakuten.com/loc/ps4-systems/82919.html",
            "html": cache.load("http://www.rakuten.com/loc/ps4-systems/82919.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 40,
                    "pages": 26,
                    "url": "http://www.rakuten.com/loc/ps4-systems/82919.html"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.rakuten.com/loc/ps4-systems/82919.html",
            "html": cache.load("http://www.rakuten.com/loc/ps4-systems/82919.html")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "PlayStation 4 Consoles",
                    "url": "http://www.rakuten.com/loc/ps4-systems/82919.html"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


});
