var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.restaurantsupply.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.restaurantsupply.com/chef-master-40062"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Chef Master 40062 - Butane Fuel 8 oz Can",
                    "price": "1.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.restaurantsupply.com/pub/media/catalog/product/cache/1/image/1000x1000/e9c3970ab036de70892d86c6d221abfe/4/0/40062_chef-master-1.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.restaurantsupply.com/portable-gas-ranges"
        }

        // Change to be the number of products on the page
        var x = 11;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Chef Master 40062 - Butane Fuel 8 oz Can';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1.99',
                    url: 'https://www.restaurantsupply.com/chef-master-40062',
                    image: 'https://www.restaurantsupply.com/pub/media/catalog/product/cache/1/small_image/368x368/beff4985b56e3afdbeabfc89641a4582/4/0/40062_chef-master-1.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Hurricane 63-6050 - 80,000 BTU High Output Cook Center';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '77.99',
                    url: 'https://www.restaurantsupply.com/hurricane-63-6050',
                    image: 'https://www.restaurantsupply.com/pub/media/catalog/product/cache/1/small_image/368x368/beff4985b56e3afdbeabfc89641a4582/6/3/63-6050_hurricane-products-1.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.restaurantsupply.com/fruit-and-vegetable-cutters-and-slicers-replacement-parts-and-accessories"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 5
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.restaurantsupply.com/fruit-and-vegetable-cutters-and-slicers-replacement-parts-and-accessories"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Fruit and Vegetable Cutter and Slicer Replacement Parts and Accessories"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
