var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.revzilla.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.revzilla.com/motorcycle/revit-eclipse-womens-jacket",
            "html": cache.load("https://www.revzilla.com/motorcycle/revit-eclipse-womens-jacket")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "REV'IT! Eclipse Women's Jacket",
                    "price": "179.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.revzilla.com/product_images/0250/7484/revit_eclipse_womens_jacket_black_750x750.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.revzilla.com/motorcycle/oxford-continental-pants",
            "html": cache.load("https://www.revzilla.com/motorcycle/oxford-continental-pants")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Oxford Continental Pants",
                    "price": "229.95",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.revzilla.com/product_images/0409/2674/oxford_continental_pants_750x750.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.revzilla.com/womens-motorcycle-jackets-vests",
        "html": cache.load("https://www.revzilla.com/womens-motorcycle-jackets-vests")
    }

    // Change to be the number of products on the page
    var x = 96;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = "Fly Racing Street Flux Air Women's Jacket";
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '99.95',
                url: 'https://www.revzilla.com/motorcycle/fly-womens-flux-air-jacket',
                image: 'https://www.revzilla.com/product_images/0129/0942/fly_womens_flux_air_jacket_300x300.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Alpinestars Stella Bogota Drystar Jacket';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '227.47',
                url: 'https://www.revzilla.com/motorcycle/alpinestars-stella-bogota-drystar-jacket',
                image: 'https://www.revzilla.com/product_images/0097/3578/alpinestars_stella_bogota_drystar_jacket_black_grey_300x300.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.revzilla.com/womens-motorcycle-jackets-vests",
            "html": cache.load("https://www.revzilla.com/womens-motorcycle-jackets-vests")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Women's Motorcycle Jackets",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
