describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.richersounds.com/tv-projectors/tv-projectors-label/all-tvs.html",
        "html": cache.load("https://www.richersounds.com/tv-projectors/tv-projectors-label/all-tvs.html")
    }

    // Change to be the number of products on the page
    var x = 12;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = '24 inch Smart LED TV HD Ready Freeview Play';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '109',
                url: 'https://www.richersounds.com/tv-projectors/all-tvs/toshiba-24sw763db.html',
                image: 'https://www.richersounds.com/media/catalog/product/cache/f073062f50e48eb0f0998593e568d857/4/0/400298.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = '32 inch Smart LED TV 1080p HD Freeview Play';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '179',
                url: 'https://www.richersounds.com/tv-projectors/all-tvs/toshiba-32l3753.html',
                image: 'https://www.richersounds.com/media/catalog/product/cache/f073062f50e48eb0f0998593e568d857/4/0/401988.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.richersounds.com/tv-projectors/tv-projectors-label/all-tvs.html",
            "html": cache.load("https://www.richersounds.com/tv-projectors/tv-projectors-label/all-tvs.html")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "All TVs",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});


