var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.rosewholesale.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.rosewholesale.com/cheapest/chic-hooded-long-sleeves-pocket-811783.html",
            "html": cache.load("https://www.rosewholesale.com/cheapest/chic-hooded-long-sleeves-pocket-811783.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Chic Hooded Long Sleeves Printed Pocket Design Women's Hoodie - Black M",
                    "price": "1.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://gloimg.rowcdn.com/ROSE/pdm-product-pic/Clothing/2016/03/29/source-img/20160329143022_67569.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.rosewholesale.com/cheapest/lace-insert-cut-out-tank-2940519.html",
            "html": cache.load("https://www.rosewholesale.com/cheapest/lace-insert-cut-out-tank-2940519.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Lace Insert Cut Out Tank Top - Black 2xl",
                    "price": "7.77",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://gloimg.rowcdn.com/ROSE/pdm-product-pic/Clothing/2018/04/03/source-img/20180403155113_17127.JPG",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.rosewholesale.com/cheap-online/womens-outerwear-c9/",
        "html": cache.load("https://www.rosewholesale.com/cheap-online/womens-outerwear-c9/")
    }

    // Change to be the number of products on the page
    var x = 40;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Hooded High Low Double Breasted Corset Coat - Purple Xl';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '13.88',
                url: 'https://www.rosewholesale.com/cheapest/hooded-high-low-double-breasted-2338619.html',
                image: 'https://gloimg.rowcdn.com/ROSE/pdm-product-pic/Clothing/2017/11/13/goods-img/1513019588005923051.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Skew Neck Sweatshirt with Halloween Print - Earthy Xl';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '12.72',
                url: 'https://www.rosewholesale.com/cheapest/skew-neck-sweatshirt-with-halloween-2079834.html',
                image: 'https://gloimg.rowcdn.com/ROSE/pdm-product-pic/Clothing/2017/10/07/goods-img/1507317727870368091.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.rosewholesale.com/cheap-online/womens-outerwear-c9/",
            "html": cache.load("https://www.rosewholesale.com/cheap-online/womens-outerwear-c9/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Outerwear",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
