var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.royalrobbins.com/samoyed-jacket",
            "html": cache.load("https://www.royalrobbins.com/samoyed-jacket")
        }

        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload) 
            console.log(inStockPage)           
            inStockPage.name.should.be.equal('SAMOYED JACKET');
            inStockPage.price.should.be.equal("99.00");
            inStockPage.url.should.be.equal("https://www.royalrobbins.com/samoyed-jacket");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('https://static.royalrobbins.com/media/catalog/product/cache/1/image/504x617/602f0fa2c1f0d1ba5e241f914e856ff9/f/1/f18_37172_samoyed_jacket_creme_022_0646_3.jpg');
            inStockPage.country.should.be.equal('us');            
         });
 
 
    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        
        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)            
            inStockPage.name.should.be.equal('SAMOYED JACKET');
            inStockPage.price.should.be.equal("99.00");
            inStockPage.url.should.be.equal("https://www.royalrobbins.com/samoyed-jacket");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('https://static.royalrobbins.com/media/catalog/product/cache/1/image/504x617/602f0fa2c1f0d1ba5e241f914e856ff9/f/1/f18_37172_samoyed_jacket_creme_022_0646_3.jpg');
            inStockPage.country.should.be.equal('us');            
         });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.royalrobbins.com/women/skirts-dresses",
            "html": cache.load("https://www.royalrobbins.com/women/skirts-dresses")
        }
        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "name": "Skirts & Dresses",
                "url":"https://www.royalrobbins.com/women/skirts-dresses"
            })

            const pagination = Structure(payload)

            JSON.stringify(pagination).should.equal(expected);
        });

    });

    describe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "https://www.royalrobbins.com/women/skirts-dresses",
                    "html": cache.load("https://www.royalrobbins.com/women/skirts-dresses")
                }

                it('should be true when number of items and pages correct', function () {

                    var expected = JSON.stringify({
                        "items": 14,
                        "pages": null,
                        "url":"https://www.royalrobbins.com/women/skirts-dresses"
                    })
        
                    const category = Structure(payload)
        
                    JSON.stringify(category).should.equal(expected);
                });        

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.royalrobbins.com/women/skirts-dresses",
            "html": cache.load("https://www.royalrobbins.com/women/skirts-dresses")
        }

        // Change to be the number of products on the page
        var x = 14;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'JAMMER SKORT';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '60.00',
                url: 'https://www.royalrobbins.com/jammer-skort',
                image: 'https://static.royalrobbins.com/media/catalog/product/cache/1/small_image/255x255/602f0fa2c1f0d1ba5e241f914e856ff9/s/1/s18_35172_jammer_skort_abyss_779_2259_1.jpg',
                country: 'us'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'FLYNN DRESS';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '70.00',
                url: 'https://www.royalrobbins.com/flynn-dress-updated',
                image: 'https://static.royalrobbins.com/media/catalog/product/cache/1/small_image/255x255/602f0fa2c1f0d1ba5e241f914e856ff9/s/1/s18_66207_flynn_dress_asphalt_568_1664_1.jpg',
                country: 'us'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    }); 


});


