    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.runningwarehouse.com/catpage-SDMONITORS.html"
        }

        // Change to be the number of products on the page
        var x = 44;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Soleus GPS One Signature Watch Kara Goucher';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '79.95',
                    url: 'http://www.runningwarehouse.com/Soleus_GPS_One_Signature_Watch/descpage-SOLONRI.html',
                    image: 'http://img.runningwarehouse.com/watermark/rs.php?path=SOLONRI-1.jpg&nw=130',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Amphipod ZipPod Micro Pocket One Size Black';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '6.91',
                    url: 'http://www.runningwarehouse.com/Amphipod_ZipPod_Micro_Pocket/descpage-AZONO.html',
                    image: 'http://img.runningwarehouse.com/watermark/rs.php?path=AZONO-1.jpg&nw=130',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

