var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.beautybrands.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.rutlandcycling.com/bikes/road-bikes/specialized-roubaix-expert-di2-2018-carbon-road-bike-black_379958",
            "html": cache.load("https://www.rutlandcycling.com/bikes/road-bikes/specialized-roubaix-expert-di2-2018-carbon-road-bike-black_379958")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Specialized Roubaix Expert Di2 2018 Carbon Road Bike Black",
                    "price": "3678.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.rutlandcycling.com/images/specialized-roubaix-expert-ultegra-di2-2018-carbon-road-bike-black-rutland-cycling.jpg?width=1534&height=1150&quality=85&mode=pad&bgcolor=ffffff&format=jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.rutlandcycling.com/bikes/road-bikes/giant-contend-sl-2-disc-2018-road-bike-grey_381950",
            "html": cache.load("https://www.rutlandcycling.com/bikes/road-bikes/giant-contend-sl-2-disc-2018-road-bike-grey_381950")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Giant Contend SL 2 Disc 2018 Road Bike Grey",
                    "price": "848.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.rutlandcycling.com/images/giant-contend-sl-2-disc-2018-road-bike-grey-rutland-cycling.jpg?width=1534&height=1150&quality=85&mode=pad&bgcolor=ffffff&format=jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.rutlandcycling.com/bikes/gt",
        "html": cache.load("https://www.rutlandcycling.com/bikes/gt")
    }

    // Change to be the number of products on the page
    var x = 8;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Return GT Grade CX Rival 2017 Cyclocross Road Bike Red 56 Inch';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '860.19',
                url: 'https://www.rutlandcycling.com/bikes/road-bikes/return-gt-grade-cx-rival-2017-cyclocross-road-bike-red-56-inch_460207',
                image: 'https://www.rutlandcycling.com/images/gt-grade-cx-rival-2017-cyclocross-road-bike-red-rutland-cycling.jpg?width=350&height=350&format=jpg&quality=70&scale=both',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'GT Grade CX Rival 2017 Cyclocross Road Bike Red';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '899.99',
                url: 'https://www.rutlandcycling.com/bikes/road-bikes/gt-grade-cx-rival-2017-cyclocross-road-bike-red_378753',
                image: 'https://www.rutlandcycling.com/images/gt-grade-cx-rival-2017-cyclocross-road-bike-red-rutland-cycling.jpg?width=350&height=350&format=jpg&quality=70&scale=both',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.rutlandcycling.com/bikes/gt",
            "html": cache.load("https://www.rutlandcycling.com/bikes/gt")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "GT Bicycles",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
