    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.ryman.co.uk/office-supplies/storage-boxes/cardboard-archive-boxes"
        }

        // Change to be the number of products on the page
        var x = 28;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Ryman Archive Boxes Pack of 5';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '9.99',
                    url: 'http://www.ryman.co.uk/ryman-archive-boxes-pack-of-5',
                    image: 'http://ryman.resultspage.com/thumb.php?s=229&f=https%3a%2f%2fwww.ryman.co.uk%2fmedia%2fcatalog%2fproduct%2f0%2f1%2f0140076799.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Bankers Box by Fellowes System Large Storage Box with Fastfold';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '49.99',
                    url: 'http://www.ryman.co.uk/bankers-box-by-fellowes-system-large-storage-box-with-fastfold',
                    image: 'http://ryman.resultspage.com/thumb.php?s=229&f=https%3a%2f%2fwww.ryman.co.uk%2fmedia%2fcatalog%2fproduct%2f0%2f1%2f0164110001.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.ryman.co.uk/office-supplies/storage-boxes"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 30,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.ryman.co.uk/office-supplies/storage-boxes/cardboard-archive-boxes"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Cardboard Archive Boxes"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

