var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.saksfifthavenue.com/main/ProductDetail.jsp?FOLDER%3C%3Efolder_id=2534374306622829&PRODUCT%3C%3Eprd_id=845524447064537&R=887444843062&P_name=Derek+Lam+10+Crosby&N=306622829&bmUID=lziCINq"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Mini Ave Leather Crossbody Bag",
                    "price": "541.83",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://image.s5a.com/is/image/saks/0400093262686_V486x648?fit=constrain,1&wid=492&hei=656',
                    "country": 'uk',
                    "description": 'Chic leather crossbody bag with lustrous hardwareTop handle, about 4" dropAdjustable crossbody strap, 19" - 23" dropSpring ring lockGoldtone hardwareOne outside slip pocketOne inside slip pocketLined8" W X 10" H X 4" DLeather/nappa leatherMade in ItalyIncludes dust bagStyle code : 0400093262686'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.saksfifthavenue.com/main/ProductDetail.jsp?FOLDER%3C%3Efolder_id=2534374306622829&PRODUCT%3C%3Eprd_id=845524447043541&R=8057943367860&P_name=Prada&N=306622829&bmUID=lziCINj"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Nylon Bow Wristlet",
                    "price": null,
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://image.s5a.com/is/image/saks/0400092600635_V486x648?fit=constrain,1&wid=492&hei=656',
                    "country": 'uk',
                    "description": 'Convertible nylon wristlet with wide ruched bowAdjustable wrist strapTop zip closureGoldtone hardwareOne inside zip pocketFully lined9.25"W X 5.5"HNylonMade in ItalyStyle code : 0400092600635'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.saksfifthavenue.com/Handbags/shop/_/N-52jzot/Ne-6lvnb5?FOLDER%3C%3Efolder_id=&bmUID=lziCIvk"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 180,
                    "pages": 20
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.saksfifthavenue.com/Handbags/shop/_/N-52jzot/Ne-6lvnb5?FOLDER%3C%3Efolder_id=&bmUID=lziCIvk"
        }

        // Change to be the number of products on the page
        var x = 180;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Hudson Leather Tote';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '746.06',
                    url: 'http://www.saksfifthavenue.com/main/ProductDetail.jsp?FOLDER%3C%3Efolder_id=2534374306622829&PRODUCT%3C%3Eprd_id=845524447064535&R=887444818831&P_name=Derek+Lam+10+Crosby&N=306622829&bmUID=lziCIKz',
                    image: 'http://image.s5a.com/is/image/saks/0400093262856_247x329.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Mini Ave Leather Crossbody Bag';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '541.83',
                    url: 'http://www.saksfifthavenue.com/main/ProductDetail.jsp?FOLDER%3C%3Efolder_id=2534374306622829&PRODUCT%3C%3Eprd_id=845524447064537&R=887444843062&P_name=Derek+Lam+10+Crosby&N=306622829&bmUID=lziCINq',
                    image: 'http://image.s5a.com/is/image/saks/0400093262686_247x329.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.saksfifthavenue.com/Handbags/shop/_/N-52jzot/Ne-6lvnb5?FOLDER%3C%3Efolder_id=&bmUID=lziCIvk"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Shop All"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


});
