var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.saksoff5th.com/main/ProductDetail.jsp?FOLDER%3C%3Efolder_id=2534374302023738&PRODUCT%3C%3Eprd_id=845524442141527&R=400921589682&P_name=3.1+Phillip+Lim&N=302023738&bmUID=lACuCfA"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Ruffle-Trimmed Silk Tank Dress",
                    "price": "154.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://s7d9.scene7.com/is/image/saksoff5th/0400092013016_ASTL?fit=constrain,1&wid=450&hei=500&fmt=jpg&op_usm%3D1.2%2C1%2C10%2C0%26resmode%3Dsharp%26iccEmbed%3d1%26icc%3DsRGB%20IEC61966-2.1%26op_saturation%3D-15',
                    "country": 'us',
                    "description": 'Cascading ruffle lends dimension to silk tank dressScoopneckSingle flutter sleeveAsymmetrical ruffle trimPullover styleAbout 37" from shoulder to hemSilkDry cleanImportedModel shown is 5\'10" (177cm) wearing US size 4.Style code : 0400092013016'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.saksoff5th.com/Women/Apparel/Dresses/shop/_/N-4ztf0a/Ne-6ja3nn"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 180,
                    "pages": 20
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.saksoff5th.com/Women/Apparel/Dresses/shop/_/N-4ztf0a/Ne-6ja3nn"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "DRESSES"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.saksoff5th.com/Women/Apparel/Dresses/shop/_/N-4ztf0a/Ne-6ja3nn"
        }

        // Change to be the number of products on the page
        var x = 180;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Ruffle-Trimmed Silk Tank Dress';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '154.99',
                    url: 'http://www.saksoff5th.com/main/ProductDetail.jsp?FOLDER%3C%3Efolder_id=2534374302023738&PRODUCT%3C%3Eprd_id=845524442141527&R=400921589682&P_name=3.1+Phillip+Lim&N=302023738&bmUID=lACuCfA',
                    image: 'http://image.s5a.com/is/image/saksoff5th/0400092013016_247x329.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Nelcira Dress';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '219.99',
                    url: 'http://www.saksoff5th.com/main/ProductDetail.jsp?FOLDER%3C%3Efolder_id=2534374302023738&PRODUCT%3C%3Eprd_id=845524442147962&R=400921202642&P_name=Calypso+St.+Barth&N=302023738&bmUID=lACuCin',
                    image: 'http://image.s5a.com/is/image/saksoff5th/0400092120265_247x329.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
