var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.samsonite.co.uk', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.samsonite.co.uk/xbr-laptop-backpack-396cm-15.6inch-black/75215-1041.html?cgid=SAMS045",
            "html": cache.load("https://www.samsonite.co.uk/xbr-laptop-backpack-396cm-15.6inch-black/75215-1041.html?cgid=SAMS045")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "XBR Laptop Backpack 15.6\"",
                    "price": "105.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.samsonite.co.uk/dw/image/v2/AATF_PRD/on/demandware.static/-/Sites-samsonite-product-catalog/default/dw8a1296a0/images/gene/prod/zoom/75215-1041_01.jpg?sw=400&sh=600&sm=fit",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.samsonite.co.uk/colorshield-laptop-sleeve-33.8cm-13.3inch-purple-pink/58130-2398.html",
            "html": cache.load("https://www.samsonite.co.uk/colorshield-laptop-sleeve-33.8cm-13.3inch-purple-pink/58130-2398.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Colorshield Laptop Sleeve 13.3\"",
                    "price": "29.00",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.samsonite.co.uk/dw/image/v2/AATF_PRD/on/demandware.static/-/Sites-samsonite-product-catalog/default/dw034232a2/images/gene/prod/zoom/58130-2398_01.jpg?sw=400&sh=600&sm=fit",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.samsonite.co.uk/laptop-backpacks/",
        "html": cache.load("https://www.samsonite.co.uk/laptop-backpacks/")
    }

    // Change to be the number of products on the page
    var x = 12;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Openroad Lady';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '119.00',
                url: 'https://www.samsonite.co.uk/openroad-lady-laptop-backpack--14.1inch-black/111053-1041.html?cgid=SAMS045',
                image: 'https://www.samsonite.co.uk/dw/image/v2/AATF_PRD/on/demandware.static/-/Sites-samsonite-product-catalog/default/dw2ac38c66/images/gene/prod/medi/111053-1041.jpg?sw=208&sh=340&sm=fit',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Rewind Natural';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '85.00',
                url: 'https://www.samsonite.co.uk/rewind-natural-laptop-backpack-l-expandable--river-blue/106784-C226.html?cgid=SAMS045',
                image: 'https://www.samsonite.co.uk/dw/image/v2/AATF_PRD/on/demandware.static/-/Sites-samsonite-product-catalog/default/dw9852a376/images/gene/prod/medi/106784-c226.jpg?sw=208&sh=340&sm=fit',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.samsonite.co.uk/laptop-backpacks/",
            "html": cache.load("https://www.samsonite.co.uk/laptop-backpacks/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Laptop Backpacks",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
