var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.sanuk.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.sanuk.com/womens-shoes/toasty-tails-short/1015711.html?dwvar_1015711_color=CHE"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "TOASTY TAILS SHORT",
                    "price": "70",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://demandware.edgesuite.net/sits_pod21/dw/image/v2/AAFF_PRD/on/demandware.static/-/Sites-masterCatalogSanuk/default/dwb4871568/zoom/1015711-CHE_1.jpg?sw=556&sh=680&sm=fit',
                    "country": 'us',
                    "description": "There's not doubt a'boot itâ€¦ No cozy, no mosey!"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.sanuk.com/mens-shoes/offshore/737045712901.html"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "OFFSHORE",
                    "price": "42",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://sits-pod21.demandware.net/dw/image/v2/AAFF_PRD/on/demandware.static/-/Sites-masterCatalogSanuk/default/dw0497eb2c/zoom/SMF10669-BLK_1.jpg?sw=556&sh=680&sm=fit',
                    "country": 'us',
                    "description": 'Comfort my friend, is blowing in the wind.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Search page pagination', function() {

    //     var payload = {
    //         "pageType" : "pagination",
    //         "url" : ""
    //     }

    //     it('should be true when number of items and pages correct', function (done) {

    //         Structure(payload, function(pagination) {
    //             var expected = JSON.stringify({
    //                 "items": "",
    //                 "pages": ""
    //             })
    //             JSON.stringify(pagination).should.equal(expected);
    //             done()
    //         })
    //     });

    // })
     describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.sanuk.com/womens-view-all/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Womens"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.sanuk.com/womens-view-all/"
        }

        // Change to be the number of products on the page
        var x = 101;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Ivana Chukka';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '90',
                    url: 'http://www.sanuk.com/womens-shoes/ivana-chukka/1014521.html?dwvar_1014521_color=TOB',
                    image: 'http://demandware.edgesuite.net/sits_pod21/dw/image/v2/AAFF_PRD/on/demandware.static/-/Sites-masterCatalogSanuk/default/dwee63ed44/zoom/1014521-TOB_2.jpg?sw=250&sh=279&sm=fit',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Yoga Mat';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '65',
                    url: 'http://www.sanuk.com/accessories/yoga-mat/1016586.html?dwvar_1016586_color=MMTST',
                    image: 'http://demandware.edgesuite.net/sits_pod21/dw/image/v2/AAFF_PRD/on/demandware.static/-/Sites-masterCatalogSanuk/default/dwf9dae925/zoom/1016586-MMTST_1.jpg?sw=250&sh=279&sm=fit',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
