var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.schoolstationery.co.uk/school-stationery/books-pads-albums/manuscript-books/unspecified/buy/112556/"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Black n Red BLACK (B5) Business Journal Soft Cover 90g/m2 Numbered Pages 400051203",
                    "price": "10.52",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.schoolstationery.co.uk//images/products/112556-300/Black-n-Red-BLACK-(B5)-Business-Journal-Soft-Cover.jpg',
                    "country": 'uk',
                    "barcode": "5010356023115"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.schoolstationery.co.uk/school-stationery/books-pads-albums/manuscript-books/unspecified/buy/J99085/"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Black n Red A4 Book Wirebound 90gsm Quadrille 5mm 140 Pages (Pack 5) 100080201",
                    "price": "35.90",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://www.schoolstationery.co.uk//images/products/J99085-300/Black-n-Red-A4-Book-Wirebound-90gsm-Quadrille-5mm-.jpg',
                    "country": 'uk',
                    "barcode": "5010356910804"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.schoolstationery.co.uk/school-stationery/books-pads-albums/manuscript-books/unspecified/"
        }

        // Change to be the number of products on the page
        var x = 12;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Black n Red A4 Book Wirebound 90gsm Quadrille 5mm 140 Pages (Pack 5) 100080201';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '29.92',
                    url: 'www.schoolstationery.co.uk/school-stationery/books-pads-albums/manuscript-books/unspecified/buy/J99085/',
                    image: 'www.schoolstationery.co.uk/images/products/J99085-100/Black-n-Red-A4-Book-Wirebound-90gsm-Quadrille-5mm-.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Sigel Conceptum Classic (A5) Lined Softcover Notebook (Black) [2 For 1] January-March 2016 CO222-XX801';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '11.53',
                    url: 'www.schoolstationery.co.uk/school-stationery/books-pads-albums/manuscript-books/unspecified/buy/02265X/',
                    image: 'www.schoolstationery.co.uk/images/products/02265X-100/Sigel-Conceptum-Classic-(A5)-Lined-Softcover-Noteb.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });


     describe('Search page pagination', function() {

    //     var payload = {
    //         "pageType" : "pagination",
    //         "url" : "http://www.schoolstationery.co.uk/school-stationery/books-pads-albums/manuscript-books/unspecified/"
    //     }

    //     it('should be true when number of items and pages correct', function (done) {

    //         Structure(payload, function(pagination) {
    //             var expected = JSON.stringify({
    //                 "items": 200,
    //                 "pages": 1
    //             })
    //             JSON.stringify(pagination).should.equal(expected);
    //             done()
    //         })
    //     });

     });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.schoolstationery.co.uk/school-stationery/books-pads-albums/manuscript-books/unspecified/"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Manuscript Books"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
