var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.scienceinsport.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.scienceinsport.com/shop-by-sport/cycling/800ml-pro-yellow-bottle",
            "html": cache.load("https://www.scienceinsport.com/shop-by-sport/cycling/800ml-pro-yellow-bottle")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "800ml Pro Yellow Bottle",
                    "price": "5.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.scienceinsport.com/media/catalog/product/cache/c687aa7517cf01e65c009f6943c2b1e9/p/r/product_sis_800ml_yellow_bottle_768x768.png",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.scienceinsport.com/football/special-offers/football-energy-bundle-2",
            "html": cache.load("https://www.scienceinsport.com/football/special-offers/football-energy-bundle-2")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Football Energy Bundle",
                    "price": "10.80",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.scienceinsport.com/media/catalog/product/cache/c687aa7517cf01e65c009f6943c2b1e9/e/n/energy_bundle_768x768.png",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.scienceinsport.com/shop-by-sport/cycling",
        "html": cache.load("https://www.scienceinsport.com/shop-by-sport/cycling")
    }

    // Change to be the number of products on the page
    var x = 15;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Endurance Pack';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '14.00',
                url: 'https://www.scienceinsport.com/shop-by-sport/cycling/endurance-pack',
                image: 'https://www.scienceinsport.com/media/catalog/product/cache/9d0981d1fb6f4f8af2e4504f8532546a/e/u/eu_endurance_pack_768x768_2.png',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Protein Bar Bundle';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '12.00',
                url: 'https://www.scienceinsport.com/shop-by-sport/cycling/sis-protein-bar-bundle',
                image: 'https://www.scienceinsport.com/media/catalog/product/cache/9d0981d1fb6f4f8af2e4504f8532546a/p/r/protein_bar_2_for_15.png',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.scienceinsport.com/shop-by-sport/cycling",
            "html": cache.load("https://www.scienceinsport.com/shop-by-sport/cycling")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Cycling",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
