var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.selfridges.com/GB/en/cat/polo-ralph-lauren-slim-fit-single-cuff-cotton-shirt_434-88064526-A04W37CQC55AK/?previewAttribute=1138a+force+blu"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Slim-fit single-cuff cotton shirt",
                    "price": "45.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://images.selfridges.com/is/image//selfridges/434-88064526-A04W37CQC55AK_1138AFORCEBLU_M?$PDP_M_ALL$',
                    "country": 'uk',
                    "description": 'Polo Ralph Lauren masters clean-cut yet carefree dressing with its preppy separates and this check shirt offers the perfect example. Crafted from pure cotton for a lightweight, breathable finish, the slim-fitting silhouette lends this timeless style a contemporary update.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.selfridges.com/GB/en/cat/mens/clothing/?browsing_country=GB&currency=GBP&language=en&pn=1&ppp=180"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 180,
                    "pages": 37
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.selfridges.com/GB/en/cat/mens/clothing/?browsing_country=GB&currency=GBP&language=en&pn=1&ppp=180"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Designer Menswear"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.selfridges.com/GB/en/cat/mens/clothing/?browsing_country=GB&currency=GBP&language=en&pn=1&ppp=180"
        }

        // Change to be the number of products on the page
        var x = 180;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Slim-fit single-cuff cotton shirt';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '45.00',
                    url: 'http://www.selfridges.com/GB/en/cat/polo-ralph-lauren-slim-fit-single-cuff-cotton-shirt_434-88064526-A04W37CQC55AK/?previewAttribute=1138a+force+blu',
                    image: 'http://images.selfridges.com/is/image/selfridges/434-88064526-A04W37CQC55AK_1138AFORCEBLU_M?$PLP_ALL$ ',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'W-velvet wool-blend coat';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '175.00',
                    url: 'http://www.selfridges.com/GB/en/cat/diesel-w-velvet-wool-blend-coat_905-10023-WVELVETJACKET0JAMG/?previewAttribute=Grey',
                    image: 'http://images.selfridges.com/is/image/selfridges/905-10023-WVELVETJACKET0JAMG_GREY_M?$PLP_ALL$ ',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
