var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.shop.bt.com/products/microsoft-halo--master-chief-collection-for-xbox-one-rq2-00018-9SYN.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Microsoft HALO: Master Chief Collection for Xbox One",
                    "price": "46.60",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.shop.bt.com/images/product/uni2/DigitalContent/9s/9SYN_DA9FCB34-C094-4E3A-ACFB-B284851923E5_large.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.shop.bt.com/category/electronics,gaming-and-gadgets,games/11170"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 11,
                    "pages": 1
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    //Category name not within an element.

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.shop.bt.com/category/sound-and-vision,home-monitoring,baby-monitors/11263-50910"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Baby Monitors"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.shop.bt.com/category/computing,laptops-and-tablets,bags-and-cases/15062"
        }

        // Change to be the number of products on the page
        var x = 13;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'HP Mobile Printer and Notebook Case - Notebook / printer carrying case - 15.5';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '73.75',
                    url: 'https://www.shop.bt.com/products/hp-mobile-printer-and-notebook-case---notebook---printer-carrying-case---15-5--q6282a-7MSJ.html?catid=15062',
                    image: 'https://www.shop.bt.com/images/product/uni2/7m/7msj_merch.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Belkin Neoprene Sleeve for Notebooks up to 15.6\' Chocolate';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '17.28',
                    url: 'https://www.shop.bt.com/products/belkin-neoprene-sleeve-for-notebooks-up-to-15-6---chocolate-f8n160earl-7XM5.html?catid=15062',
                    image: 'https://www.shop.bt.com/images/product/uni2/7x/7xm5_merch.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
