var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.shopdisney.co.uk', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.shopdisney.co.uk/danielle-nicole-cogsworth-crossbody-bag-427245755135.html?cgid=2000020",
            "html": cache.load("https://www.shopdisney.co.uk/danielle-nicole-cogsworth-crossbody-bag-427245755135.html?cgid=2000020")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Danielle Nicole Cogsworth Crossbody Bag",
                    "price": "50.00",
                    "url": payload.url,
                    "inStock": 'UNCHECKED',
                    "stockLevel": null,
                    "image": "https://s7d9.scene7.com/is/image/DisneyStoreES/427245755135?$sdLargeD$&defaultImage=no%20image-image_uk",
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        xit('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.shopdisney.co.uk/departments/womens-fashion/bags-and-accessories/bags",
            "html": cache.load("https://www.shopdisney.co.uk/departments/womens-fashion/bags-and-accessories/bags")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Bags",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "https://www.shopdisney.co.uk/departments/womens-fashion/bags-and-accessories/bags",
                    "html": cache.load("https://www.shopdisney.co.uk/departments/womens-fashion/bags-and-accessories/bags")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": 48,
                            "pages": 2,
                            "url": payload.url
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.shopdisney.co.uk/departments/womens-fashion/bags-and-accessories/bags",
            "html": cache.load("https://www.shopdisney.co.uk/departments/womens-fashion/bags-and-accessories/bags")
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Disney Store The Nightmare Before Christmas Rolling Luggage';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '40.00',
                    url: 'https://www.shopdisney.co.uk/disney-store-the-nightmare-before-christmas-rolling-luggage-427248638282.html?cgid=2000020',
                    image: 'https://s7d9.scene7.com/is/image/DisneyStoreES/427248638282?$sdMedium$&defaultImage=no%20image-image_uk',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Samsonite Star Wars Large Wheeled Duffle Bag';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '199.00',
                    url: 'https://www.shopdisney.co.uk/samsonite-star-wars-large-wheeled-duffle-bag-541484782647.html?cgid=2000020',
                    image: 'https://s7d9.scene7.com/is/image/DisneyStoreES/541484782647?$sdMedium$&defaultImage=no%20image-image_uk',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


