describe('Search page category', function () {

    var payload = {
        "pageType": "category",
        "url": "https://www.shopdisney.com/toys/action-figures",
        "html": cache.load("https://www.shopdisney.com/toys/action-figures")
    }

    it('should be true when number of items and pages correct', function (done) {

        Structure(payload, function (err, category) {
            var expected = JSON.stringify({
                "name": "",
                "url":"https://www.shopdisney.com/toys/action-figures"
            })
            JSON.stringify(category).should.equal(expected);
            done()
        })
    });

});


describe('In Stock Page', function () {

    var payload = {
        "pageType": "product",
        "url": "https://www.shopdisney.com/captain-america-action-figure-marvel-toybox-1467996",
        "html": cache.load("https://www.shopdisney.com/captain-america-action-figure-marvel-toybox-1467996")
    }

    it('should be true when product is in stock', function (done) {

        Structure(payload, function (err, inStockPage) {
            var expected = JSON.stringify({
                "name": "Captain America Action Figure - Marvel Toybox",
                "price": "12.95",
                "url": payload.url,
                "inStock": true,
                "stockLevel": null,
                "image": "https://lumiere-a.akamaihd.net/v1/images/file_827c58a2.jpeg?width=1200&region=0%2C0%2C2000%2C2000",
                "country": "us"
            })
            JSON.stringify(inStockPage).should.equal(expected);
            done()
        })
    });

});


