var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.shopgadgetsandgizmos.com/product/25717/163/"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "9\" Rail Road Crossing Light",
                    "price": "12.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://www.shopgadgetsandgizmos.com/displayImage.php?imgID=51930",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": "",
    //                 "country": "us"
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.shopgadgetsandgizmos.com/products/section/163/"
        }

        // Change to be the number of products on the page
        var x = 25;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = '9" Rail Road Crossing Light';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '12.99',
                    url: 'http://www.shopgadgetsandgizmos.com/product/25717/163/',
                    image: 'http://www.shopgadgetsandgizmos.com/displayImage.php?imgID=51932',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Top Tech Audio BALL108SL HI-Tec Bass Outdoor Sound Set Trolley Speaker';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '179.99',
                    url: 'http://www.shopgadgetsandgizmos.com/product/29360/163/',
                    image: 'http://www.shopgadgetsandgizmos.com/displayImage.php?imgID=65196',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
