    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.shopjustice.com/the-collections/new-arrivals/shoes-accessories/P-10335"
        }

        // Change to be the number of products on the page
        var x = 62;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Heart Felt Floppy Hat';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '16.90',
                    url: 'http://www.shopjustice.com/heart-felt-floppy-hat/prd-7636746',
                    image: 'http://shopjustice.scene7.com/is/image/justiceProdATG/7636746_610?$large$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Rhinestone Critter Watch';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '16.90',
                    url: 'http://www.shopjustice.com/rhinestone-critter-watch/prd-7381381',
                    image: 'http://shopjustice.scene7.com/is/image/justiceProdATG/7381381_697?$large$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

