var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.shopping.com/digital-cameras/products"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 36,
                    "pages": 9
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.shopping.com/digital-cameras/products"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Digital Cameras"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.shopping.com/toys/products"
        }

        // Change to be the number of products on the page
        var x = 40;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "Big Backyard Sandy Cove Swing...";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '549.00',
                    url: 'http://rover.ebay.com/rover/13/0/19/DealFrame/DealFrame.cmp?bm=825&BEFID=96425&aon=%5E&MerchantID=518814&crawler_id=518814&dealId=W3HAIET-4xfW5gdCUI3elA%3D%3D&url=http%3A%2F%2Fc.affil.walmart.com%2Ft%2Fcsesh01%3Fl%3Dhttp%253A%252F%252Fwww.walmart.com%252Fip%252FBig-Backyard-Sandy-Cove-Swing-Set%252F33680090%253Fwmlspartner%253Dshoppingcom%2526affcmpid%253D1501955887%2526tmode%253D0000%2526bcfg%253D53788b72a7832a4952cd6f437306e91e%2526veh%253Dcse&linkin_id=3033102&Issdt=160929102554&searchID=L410HUNj02&DealName=Big+Backyard+Sandy+Cove+Swing+Set&dlprc=549.0&AR=1&NG=17&NDP=31&PN=1&ST=7&FPT=DNSRGV&NDS=&NMS=&MRS=&PD=148434539&brnId=2455&IsFtr=0&IsSmart=0&op=&CM=&RR=1&IsLps=0&code=&acode=795&category=&HasLink=&ND=&MN=&GR=&lnkId=&SKU=33680090&IsGtb=0&SPT=DSDNGV&MNID=0&EF=&sig=343d0f495b02cd81ad3bf6e8db180610ebc1e139&CT=14',
                    image: 'http://di70.shoppingshadow.com/images/di/57/33/48/414945542d3478665735676443554933656c41-149x149-0-0.jpg?rqid=p23.82daca8e94998de327e1&rqt=SRS&a=1&c=1&dealId=W3HAIET-4xfW5gdCUI3elA%3D%3D&l=3033102&r=1&pr=1&dipr=1&pid=148434539&pdid=W3HAIET-4xfW5gdCUI3elA%3D%3D',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Bamboo Garland - Garland';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '6.50',
                    url: 'http://rover.ebay.com/rover/13/0/19/DealFrame/DealFrame.cmp?bm=480&BEFID=96425&aon=%5E&MerchantID=303503&crawler_id=811366&dealId=5jsD4hoNw5WdGeO7hGzgSA%3D%3D&url=http%3A%2F%2Fwww.orientaltrading.com%2Fbpentry%3Fsku%3D34%252F1388%26BP%3DPS516%26cm_mmc%3DCSE-_-Shopping-_-Party%2BSupplies%252FParty%2BDecorations%252FGarland-_-34%252F1388&linkin_id=3033102&Issdt=160929102554&searchID=L410HUNj02&DealName=Bamboo+Garland+-+Garland&dlprc=6.5&AR=1&NG=17&NDP=31&PN=1&ST=7&FPT=DNSRGV&NDS=&NMS=&MRS=&PD=170519778&brnId=2455&IsFtr=0&IsSmart=0&op=&CM=&RR=1&IsLps=0&code=&acode=508&category=&HasLink=&ND=&MN=&GR=&lnkId=&SKU=34%2F1388&IsGtb=0&SPT=DSDNGV&MNID=0&EF=&sig=e588aa58ec896b04ac0cb73cd6e880e8abfc2983&CT=14',
                    image: 'http://di61.shoppingshadow.com/images/di/35/6a/73/4434686f4e7735576447654f3768477a675341-149x149-0-0.jpg?rqid=p23.82daca8e94998de327e1&rqt=SRS&a=1&c=1&dealId=5jsD4hoNw5WdGeO7hGzgSA%3D%3D&l=3033102&r=40&pr=40&dipr=1&pid=170519778&pdid=5jsD4hoNw5WdGeO7hGzgSA%3D%3D',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
