var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.shoptv.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.shoptv.com/empire-emblem-rocks-glass/detail.php?p=1018903&v=new-arrivals"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Empire Emblem Rocks Glass",
                    "price": "11.95",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://www.shoptv.com/imgcache/product/resized/001/018/903/catl/empire-emblem-rocks-glass_1000.jpg?k=ddc9b861&pid=1018903&s=catl&sn=shoptv",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.shoptv.com/detail.php?p=535101&ecid=AFF-7973256"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Rachael Ray Stoneware Bubble and Brown Individuals 2-Piece Set, Orange",
                    "price": "24.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://www.shoptv.com/imgcache/product/resized/000/535/101/catl/rachael-ray-stoneware-bubble-and-brown-individuals-2-piece-set-orange_1000.jpg?k=98a1084c&pid=535101&s=catl&sn=shoptv",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.shoptv.com/sale/index.php?v=sale&nvbar=Top+Navigation:Sale"
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Hollywood Game Night';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '5.00',
                    url: 'http://www.shoptv.com/hollywood-game-night/detail.php?p=508368&v=sale',
                    image: 'http://www.shoptv.com/imgcache/product/resized/000/508/368/catl/hollywood-game-night-778_305.jpg?k=51680445&pid=508368&s=catl&sn=shoptv',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Props in a Box The Princess & The Chef';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '19.97',
                    url: 'http://www.shoptv.com/props-in-a-box-the-princess-the-chef/detail.php?p=921774&v=sale',
                    image: 'http://www.shoptv.com/imgcache/product/resized/000/921/774/catl/props-in-a-box-the-princess-the-chef-739_305.jpg?k=391dfdc9&pid=921774&s=catl&sn=shoptv',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.shoptv.com/sale/index.php?v=sale&nvbar=Top+Navigation:Sale"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 24,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.shoptv.com/sale/index.php?v=sale&nvbar=Top+Navigation:Sale"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Sale"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


});
