var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.shovelandshoe.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.shovelandhoe.com/KidTuff_Grip_Gloves_p/889.htm",
            "html" : cache.load('https://www.shovelandhoe.com/KidTuff_Grip_Gloves_p/889.htm')
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "KidTuff Grip Gloves",
                    "price": "4.95",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://cdn3.volusion.com/nmtsp.cvsnp/v/vspfiles/photos/889-2T.jpg?1528722682',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.shovelandhoe.com/ProductDetails.asp?ProductCode=574",
            "html" : cache.load('https://www.shovelandhoe.com/ProductDetails.asp?ProductCode=574')
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Felco Lopping Shears F-22",
                    "price": "259.25",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://cdn3.volusion.com/nmtsp.cvsnp/v/vspfiles/photos/574-2T.jpg?1527719574',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.shovelandhoe.com/Garden_Apparel_s/94.htm",
            "html" : cache.load('https://www.shovelandhoe.com/Garden_Apparel_s/94.htm')
        }

        // Change to be the number of products on the page
        var x = 30;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = '3909W Knit Work Gloves';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1.49',
                    url: 'https://www.shovelandhoe.com/3909W_Knit_Work_Gloves_p/463.htm',
                    image: 'https://cdn3.volusion.com/nmtsp.cvsnp/v/vspfiles/photos/463-1.jpg?1527719574',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'RH-LL-30- Laser Lite Corded Ear Plug (Box of 100)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '26.60',
                    url: 'https://www.shovelandhoe.com/RH_LL_30_Laser_lite_Corded_Ear_plug_Box_of_100_p/1046.htm',
                    image: 'https://cdn3.volusion.com/nmtsp.cvsnp/v/vspfiles/photos/1046-1.jpg?1527719574',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    // describe('Search page pagination', function() {

    //     var payload = {
    //         "pageType" : "pagination",
    //         "url" : "",
    //         "html" : cache.load('')
    //     }

    //     it('should be true when number of items and pages correct', function (done) {

    //         Structure(payload, function(pagination) {
    //             var expected = JSON.stringify({
    //                 "items": 200,
    //                 "pages": 1
    //             })
    //             JSON.stringify(pagination).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.shovelandhoe.com/Garden_Apparel_s/94.htm",
            "html" : cache.load('https://www.shovelandhoe.com/Garden_Apparel_s/94.htm')
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Garden Apparel",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
