var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.sierratradingpost.com/patagonia-rio-azul-chest-waders-stockingfoot-for-men~p~103dj/?filterString=fishing~d~14%2F&colorFamily=01",
            "html": cache.load("http://www.sierratradingpost.com/patagonia-rio-azul-chest-waders-stockingfoot-for-men~p~103dj/?filterString=fishing~d~14%2F&colorFamily=01")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Patagonia Rio Azul Chest Waders - Stockingfoot (For Men)",
                    "price": "199.95",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": "http://i.stpost.com/patagonia-rio-azul-chest-waders-stockingfoot-for-men-in-narwhal-grey~p~103dj_01~460.2.jpg",
                    "country":"us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.sierratradingpost.com/clearance~1/or(mens-accessories~d~9402,womens-accessories~d~254)/?osid=mm-clearance-accessories-_-030718",
            "html": cache.load("https://www.sierratradingpost.com/clearance~1/or(mens-accessories~d~9402,womens-accessories~d~254)/?osid=mm-clearance-accessories-_-030718")
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Patagonia Rio Azul Chest Waders - Stockingfoot (For Men)';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '199.95',
                    url: 'http://www.sierratradingpost.com/patagonia-rio-azul-chest-waders-stockingfoot-for-men~p~103dj/?filterString=fishing~d~14%2F&colorFamily=01',
                    image: 'http://i.stpost.com/patagonia-rio-azul-chest-waders-stockingfoot-for-men-in-narwhal-grey~p~103dj_01~220.2.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Redington Prospector Spey Rod with Tube - 4-Piece';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '249.95',
                    url: 'http://www.sierratradingpost.com/redington-prospector-spey-rod-with-tube-4-piece~p~9713v/?filterString=fishing~d~14%2F&colorFamily=99',
                    image: 'http://i.stpost.com/redington-prospector-spey-rod-with-tube-4-piece-in-see-photo~p~9713v_99~220.2.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.sierratradingpost.com/fishing~d~14/",
            "html": cache.load("http://www.sierratradingpost.com/fishing~d~14/")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 24,
                    "pages": 27,
                    "url" : "http://www.sierratradingpost.com/fishing~d~14/"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.sierratradingpost.com/fishing~d~14/",
            "html": cache.load("http://www.sierratradingpost.com/fishing~d~14/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Fishing",
                    "url" : "http://www.sierratradingpost.com/fishing~d~14/"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
