var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.simplepastimes.com/pd-ravensburger-glue-go.cfm",
            "html" : cache.load("http://www.simplepastimes.com/pd-ravensburger-glue-go.cfm")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Ravensburger Puzzle Glue & Go",
                    "price": "4.95",
                    "url": payload.url,
                    "inStock": 'UNCHECKED',
                    "stockLevel": null,
                    "image": "http://www.simplepastimes.com/prod_images_large/63422F96-C4E2-8475-7D0F7C11E3D1DF2B.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.simplepastimes.com/cat_1000.cfm",
            "html" : cache.load("https://www.simplepastimes.com/cat_1000.cfm")
        }

        // Change to be the number of products on the page
        var x = 25;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = '13 Eagles (1000 Piece Round Puzzle by SunsOut)';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '15.95',
                    url: 'https://www.simplepastimes.com/pd-13-eagles.cfm',
                    image: 'http://www.simplepastimes.com/ihs_images/13Eagles_04541ba2de_300x300.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'A Welcome New Arrival (1000 Piece Puzzle by SunsOut)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '15.95',
                    url: 'https://www.simplepastimes.com/pd-a-welcome-new-arrival-1000-piece-puzzle-by-sunsout.cfm',
                    image: 'http://www.simplepastimes.com/ihs_images/46271awelcomenewarrivalcatweb_300x300.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.simplepastimes.com/cat_under.cfm",
            "html" : cache.load("http://www.simplepastimes.com/cat_under.cfm")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 25,
                    "pages": 6,
                    "url": payload.url
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.simplepastimes.com/cat_accessories.cfm",
            "html" : cache.load("http://www.simplepastimes.com/cat_accessories.cfm")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Accessories",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
