 describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.simplygames.com/xbox-one/games?filter=all&sortby=release-date"
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Just Dance 2017 on Xbox One';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '20.85',
                    url: 'http://www.simplygames.com/p/just-dance-2017-xbox-one',
                    image: 'http://www.simplygames.com/uploads/products/41349/41349_xsm.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = '7 Days to Die on Xbox One';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '23.85',
                    url: 'http://www.simplygames.com/p/7-days-to-die-xbox-one',
                    image: 'http://www.simplygames.com/uploads/products/40972/40972_xsm.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
