var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.skinstore.com/foreo-luna-mini-2-sunflower-yellow-us/11524814.html",
            "html": cache.load("https://www.skinstore.com/foreo-luna-mini-2-sunflower-yellow-us/11524814.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "FOREO LUNAâ„¢ mini 2 - Sunflower Yellow",
                    "price": "139.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://s1.thcdn.com/productimg/600/600/11213788-3864364073220680.jpg",
                    "country": 'us',
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.skinstore.com/skin-care/cleansers.list",
            "html": cache.load("https://www.skinstore.com/skin-care/cleansers.list")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Skin Cleansers",
                    "url":'https://www.skinstore.com/skin-care/cleansers.list'
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "https://www.skinstore.com/skin-care/cleansers.list",
                    "html": cache.load("https://www.skinstore.com/skin-care/cleansers.list")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '66',
                            "pages": '11',
                            "url":'https://www.skinstore.com/skin-care/cleansers.list'
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.skinstore.com/skin-care/cleansers.list",
            "html": cache.load("https://www.skinstore.com/skin-care/cleansers.list")
        }

        // Change to be the number of products on the page
        var x = 66;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Eve Lom Cleanser 3.3oz';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '80.00',
                    url: 'https://www.skinstore.com/eve-lom-cleanser-100ml/10365659.html?buy=10365659',
                    image: 'https://s3.thcdn.com/productimg/300/300/10365659-6984510171162656.jpg',
                    country: 'us',
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Replenix Fortified Cleanser';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '33.00',
                    url: 'https://www.skinstore.com/replenix-fortified-cleanser/11290063.html?buy=11290063',
                    image: 'https://s3.thcdn.com/productimg/300/300/11290063-1814444647455130.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


