var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.smiggle.co.uk', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.smiggle.co.uk/shop/en/smiggleuk/sale/all-sale/mega-pop-out-pencil-case?colour=BLACK",
            "html": cache.load("https://www.smiggle.co.uk/shop/en/smiggleuk/sale/all-sale/mega-pop-out-pencil-case?colour=BLACK")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Microsoft Xbox One X 1TB 4K Console",
                    "price": "449.00",
                    "url": 'https://www.tesco.com/direct/microsoft-xbox-one-x-1tb-4k-console/234-7189.prd?skuId=234-7189',
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://tesco.scene7.com/is/image/tesco/234-7189_PI_66650AL3?op_sharpen=1&id=sdDaH3&fmt=jpg&fit=constrain,1&wid=630&hei=506&op_sharpen=1",
                    "country": "uk",
                    "sku": '234-7189'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.smiggle.co.uk/shop/en/smiggleuk/food-drink/all-food-drink/game-lunch-box?colour=BLACK",
            "html": cache.load("https://www.smiggle.co.uk/shop/en/smiggleuk/food-drink/all-food-drink/game-lunch-box?colour=BLACK")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Character Minions Printed 100% Cotton Beach Towel",
                    "price": "7.40",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://tesco.scene7.com/is/image/tesco/104-9626_PI_1000540MN?op_sharpen=1&id=HzNaG0&fmt=jpg&fit=constrain,1&wid=506&hei=506&op_sharpen=1",
                    "country": "uk",
                    "sku": '104-9626'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.smiggle.co.uk/shop/en/smiggleuk/food-drink/all-food-drink?#facet:&orderBy:&facetName:&minPrice:&maxPrice:&pageSize:&clearAll:&lowAmt:&highAmt:&",
        "html": cache.load("https://www.smiggle.co.uk/shop/en/smiggleuk/food-drink/all-food-drink?#facet:&orderBy:&facetName:&minPrice:&maxPrice:&pageSize:&clearAll:&lowAmt:&highAmt:&")
    }

    // Change to be the number of products on the page
    var x = 55;
    it('should return ' + x + ' numer of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Smiggle 15Th Birthday Double Decker Lunchbox';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '18.00',
                url: 'https://www.smiggle.co.uk/shop/en/smiggleuk/food-drink/all-food-drink/15th-birthday-double-decker-lunchbox?colour=GOLD',
                image: 'https://www.smiggle.co.uk/SM/aurora/images/products/small/442115_gold_back_s.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Smiggle Keep Cosy Stainless Steel Water Bottle';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '10.00',
                url: 'https://www.smiggle.co.uk/shop/en/smiggleuk/food-drink/all-food-drink/keep-cosy-stainless-steel-water-bottle?colour=BLACK',
                image: 'https://www.smiggle.co.uk/SM/aurora/images/products/small/441758_black_back_s.jpg',
                country: 'uk',
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "https://www.tesco.com/direct/home-electrical/batteries/cat3375667.cat?catId=4294731032",
            "html": cache.load("https://www.tesco.com/direct/home-electrical/batteries/cat3375667.cat?catId=4294731032")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 1,
                    "url": "https://www.tesco.com/direct/home-electrical/batteries/cat3375667.cat?catId=4294731032"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.tesco.com/direct/home-electrical/electrical-accessories/cat3376649.cat?icid=homeelectrical_flyoutlink_BatteriesElectricalAccessories",
            "html": cache.load("http://www.tesco.com/direct/home-electrical/electrical-accessories/cat3376649.cat?icid=homeelectrical_flyoutlink_BatteriesElectricalAccessories")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Electrical Accessories",
                    "url": "http://www.tesco.com/direct/home-electrical/electrical-accessories/cat3376649.cat?icid=homeelectrical_flyoutlink_BatteriesElectricalAccessories"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});


