var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.softball.com/catalog/product.jsp?productId=41770",
            "html": cache.load("http://www.softball.com/catalog/product.jsp?productId=41770")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Champro Youth Triple Crown Knicker Baseball Pant",
                    "price": "14.95",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://di.shotfarm.com/img/12006?src=BP10Y.png&sqr=350&fmt=jpg",
                    "country": 'us',
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.softball.com/catalog/thumbnail.jsp?q=SITE_CODE:SBS%20AND%20CATEGORY_ID:5749&facet.field=SOFTBALL_DEPARTMENT_NAME&sort=PRODUCT_AFFINITY%20desc&rows=24&wt=json",
            "html": cache.load("http://www.softball.com/catalog/thumbnail.jsp?q=SITE_CODE:SBS%20AND%20CATEGORY_ID:5749&facet.field=SOFTBALL_DEPARTMENT_NAME&sort=PRODUCT_AFFINITY%20desc&rows=24&wt=json")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Uniforms - Women's - Jerseys",
                    "url": "http://www.softball.com/catalog/thumbnail.jsp?q=SITE_CODE:SBS%20AND%20CATEGORY_ID:5749&facet.field=SOFTBALL_DEPARTMENT_NAME&sort=PRODUCT_AFFINITY%20desc&rows=24&wt=json"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "",
                    "html": cache.load("")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "http://www.softball.com/catalog/thumbnail.jsp?q=SITE_CODE:SBS%20AND%20CATEGORY_ID:5749&facet.field=SOFTBALL_DEPARTMENT_NAME&sort=PRODUCT_AFFINITY%20desc&rows=24&wt=json",
            "html": cache.load("http://www.softball.com/catalog/thumbnail.jsp?q=SITE_CODE:SBS%20AND%20CATEGORY_ID:5749&facet.field=SOFTBALL_DEPARTMENT_NAME&sort=PRODUCT_AFFINITY%20desc&rows=24&wt=json")
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Champro Women\'s Racer Back Fastpitch Jersey';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '4.98',
                    url: 'http://www.softball.com/catalog/product.jsp?productId=29846',
                    image: 'http://di.shotfarm.com/img/12006?src=BS17.png&sqr=130&fmt=jpeg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Augusta Women\'s Diamond Softball Jersey';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: null,
                    url: 'http://www.softball.com/catalog/product.jsp?productId=126515',
                    image: 'http://di.shotfarm.com/img/12006?src=1225.png&sqr=130&fmt=jpeg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


