var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.sonicelectronix.com/item_46586_NVX-Audio-XPT100.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "NVX Audio XPT100",
                    "price": "79.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://images.sonicelectronix.com/images/3027153/main/xpt100.jpg',
                    "country": 'us',
                    "barcode": '818060010736'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.sonicelectronix.com/item_31638_Sennheiser-HD-650-HD650-Audiophile-Grade-Hi-Fi-Professional-Headphones-HD650.html"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Sennheiser HD 650 (HD650) Audiophile-Grade Hi-Fi Professional Headphones (HD650)",
                    "price": "339.00",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://images.sonicelectronix.com/images/2949479/main/hd650.jpg',
                    "country": 'us',
                    "barcode": '615104099692'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.sonicelectronix.com/cat_i736_over-ear-headphones.html"
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'NVX Audio XPT100';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '79.99',
                    url: 'www.sonicelectronix.com/item_46586_NVX-Audio-XPT100.html',
                    image: 'http://images.sonicelectronix.com/images/3027153/small/xpt100.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Audio Technica ATH-M20x';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '49.00',
                    url: 'www.sonicelectronix.com/item_78688_Audio-Technica-ATH-M20x.html',
                    image: 'http://images.sonicelectronix.com/images/2080531/small/athm20x.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.sonicelectronix.com/cat_i736_over-ear-headphones.html"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 20,
                    "pages": 5
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.sonicelectronix.com/cat_i736_over-ear-headphones.html"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Over-Ear Headphones"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
