    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "http://www.spacenk.com/uk/en_GB/skincare/cleansers/cleanser/",
            "html": cache.load("http://www.spacenk.com/uk/en_GB/skincare/cleansers/cleanser/")
        }

        // Change to be the number of products on the page
        var x = 132;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Eve Lom Cleanser';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '40.00',
                    url: 'https://www.spacenk.com/uk/en_GB/skincare/cleansers/cleanser/cleanser-MUK154050009.html',
                    image: 'http://www.spacenk.com/dw/image/v2/ABCE_PRD/on/demandware.static/-/Sites-spacenkmastercatalog/default/dwdfa730e4/products/EVE_LOM/UK154050009_EVE_LOM.jpg?sw=250&sh=250',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Ren Rose Centifolia Hot Cloth Cleanser';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '18.00',
                    url: 'https://www.spacenk.com/uk/en_GB/skincare/cleansers/cleanser/rose-centifolia-hot-cloth-cleanser-MUK200011937.html',
                    image: 'http://www.spacenk.com/dw/image/v2/ABCE_PRD/on/demandware.static/-/Sites-spacenkmastercatalog/default/dw68fc0e86/products/REN/UK200011937_REN.jpg?sw=250&sh=250',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.spacenk.com/us/en_US/eye-creams-and-balms/",
            "html": cache.load("http://www.spacenk.com/us/en_US/eye-creams-and-balms/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "eyecare",
                    "url": "http://www.spacenk.com/us/en_US/eye-creams-and-balms/"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

