var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('www.staples.com', function() {

    before(function()  {
        this.timeout(5000);
    })

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.staples.co.uk/as-120-desktop-calculator/cbs/297875708.html?promoCode=400600999&Effort_Code=WW&Find_Number=297875708&mT=0&isSubscription=False",
            "html" : cache.load("https://www.staples.co.uk/as-120-desktop-calculator/cbs/297875708.html?promoCode=400600999&Effort_Code=WW&Find_Number=297875708&mT=0&isSubscription=False")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Canon AS-120 Desktop Calculator",
                    "price": "5.94",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.staples.co.uk/content/images/product/GenesisExtraLarge/58/17/asset.235817.jpg",
                    "country": "uk",
                    "description": "Model: AS-120 Product Type: Desktop calculator Number Of Digits: 12",
                    "sku":"297875708"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.staples.co.uk/mechanical/cbs/224647133.html?promoCode=&Effort_Code=WW&Find_Number=224647133&m=0",
            "html" : cache.load("http://www.staples.co.uk/mechanical/cbs/224647133.html?promoCode=&Effort_Code=WW&Find_Number=224647133&m=0")
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Faber-Castell POLY MATIC Mechanical Pencil 0.7 mm Tip Cushioned Lead With Twist-Out Eraser Assorted Colours (blister 2 each)",
                    "price": "2.50",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.staples.co.uk/content/images/product/GenesisExtraLarge/77/74/asset.2487774.jpg",
                    "country": "uk",
                    "description": "Retractable: Yes Grip Present: Yes Max Line Width: 0.7 mm",
                    "sku":"224647133"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.staples.co.uk/calculators-desktop-handheld-pocket/cbk/184.html",
            "html" : cache.load("http://www.staples.co.uk/calculators-desktop-handheld-pocket/cbk/184.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Calculators Desktop HandheldPocket",
                    "url":"http://www.staples.co.uk/calculators-desktop-handheld-pocket/cbk/184.html"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.staples.co.uk/heating-cooling-and-air-quality/cbc/1451.html",
            "html" : cache.load("https://www.staples.co.uk/heating-cooling-and-air-quality/cbc/1451.html")
        }

        // Change to be the number of products on the page
        var x = 22;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Fellowes Carbon Filter, Medium';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '18.59',
                    url: 'https://www.staples.co.uk/carbon-filter-medium/cbs/297880599.html?promoCode=&Effort_Code=WW&Find_Number=297880599&m=0&isSubscription=False',
                    image: 'https://www.staples.co.uk/content/images/product/GenesisBanner/03/69/asset.160369.jpg',
                    country: 'uk',
                    sku:"297880599"
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Fellowes Aeromax DX55 Air Purifier';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '262.80',
                    url: 'https://www.staples.co.uk/air-cleaners/cbs/297687836.html?promoCode=&Effort_Code=WW&Find_Number=297687836&m=0&isSubscription=False',
                    image: 'https://www.staples.co.uk/content/images/product/GenesisBanner/54/15/asset.1285415.jpg',
                    country: 'uk',
                    sku:"297687836"
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});



