var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('www.staples.com', function() {

     this.timeout(5000);

     describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.staples.com/Dining-Entertainment/cat_CG3409?supercategory=&bopis=false&page=3",
            "html": cache.load("https://www.staples.com/Dining-Entertainment/cat_CG3409?supercategory=&bopis=false&page=3")
        }

        it('should return 13 products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(13);
                done()
            })
        });


        it('first product should be', function(done) {

            Structure(payload, function(err, result) {

                 var first = result.shift()
                    var expected = JSON.stringify({
                        barcode: null,
                        name: 'InterDesign Forma Coasters, Brushed Stainless Steel/Black, 4/Set...\\n(2)',
                        price: '16.49',
                        url: 'http://www.staples.com/Post-it-Original-Notes-3-x-3-Cape-Town-Collection-Cabinet-Pack-18-Pack/product_1611322',
                        image: 'http://www.staples-3p.com/s7/is/image/Staples/s0963234_sc7?$std$',
                        country: 'us'
                    })

                    JSON.stringify(first).should.equal(expected)
                    done()
                })

        })

      it('last product should be a Staples® Stickies™ Recycled Pop-up Notes, 3" x 3", Yellow, 12 Pads/Pack (S-33YRP12)', function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Staples® Stickies™ Recycled Pop-up Notes, 3" x 3", Yellow, 12 Pads/Pack (S-33YRP12)',
                    price: '16.99',
                    url: 'http://www.staples.com/Staples-reg-Stickies-trade-Recycled-Pop-up-Notes-3-x-3-Yellow-12-Pads-Pack-S-33YRP12-/product_565438',
                    image: 'http://www.staples-3p.com/s7/is/image/Staples/s0196139_sc7?$std$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.staples.com/Staples-reg-Stickies-trade-Recycled-Pop-up-Notes-3-x-3-Yellow-12-Pads-Pack-S-33YRP12-/product_565438",
            "html": cache.load("http://www.staples.com/Staples-reg-Stickies-trade-Recycled-Pop-up-Notes-3-x-3-Yellow-12-Pads-Pack-S-33YRP12-/product_565438")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    name: 'Staples® Stickies™ Recycled Pop-up Notes, 3" x 3", Yellow, 12 Pads/Pack (S-33YRP12)',
                    price: '16.99',
                    url: payload.url,
                    inStock: true,
                    stockLevel: null,
                    image: 'http://www.staples-3p.com/s7/is/image/Staples/s0196139_sc7?$splssku$',
                    country: 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should return false when product is out of stock', function(done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 name: '',
    //                 price: '',
    //                 url: null,
    //                 inStock: false,
    //                 stockLevel: null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     })

    // })

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.staples.com/Post-It-Stickies-Notes/cat_CL165755",
            "html": cache.load("http://www.staples.com/Post-It-Stickies-Notes/cat_CL165755")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 24,
                    "pages": 12,
                    "url" : "http://www.staples.com/Post-It-Stickies-Notes/cat_CL165755"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.staples.com/Post-It-Stickies-Notes/cat_CL165755",
            "html": cache.load("http://www.staples.com/Post-It-Stickies-Notes/cat_CL165755")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Post-it® & Stickies™ Notes",
                    "url" : "http://www.staples.com/Post-It-Stickies-Notes/cat_CL165755"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

 });


