    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.statelinetack.com/search.aspx?path=cSTATELINETACKp2552&page=1&hits=2147483647&sort=bestselling"
        }

        // Change to be the number of products on the page
        var x = 88;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Hobby Horse Ladies Black Simplicity Chaps';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '219.99',
                    url: 'http://www.statelinetack.com/item/hobby-horse-ladies-black-simplicity-chaps/E009622/',
                    image: 'http://s7d5.scene7.com/is/image/PetsUnited/TE009622?wid=160&hei=160&fmt=jpeg&qlt=75,0&op_sharpen=0&resMode=sharp2&op_usm=0.9,1.0,6,0&iccEmbed=0&',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Hobby Horse Ladies Tori Vest';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '134.96',
                    url: 'http://www.statelinetack.com/item/hobby-horse-ladies-tori-vest/E016218/',
                    image: 'http://s7d5.scene7.com/is/image/PetsUnited/TE016218?wid=160&hei=160&fmt=jpeg&qlt=75,0&op_sharpen=0&resMode=sharp2&op_usm=0.9,1.0,6,0&iccEmbed=0&',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

