    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.steinmart.com/category/for+the+home/home+decor/candles-and-fragrance.do"
        }

        // Change to be the number of products on the page
        var x = 99;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Exclusively Ours - Floriana 12oz Scented Candle';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '15.99',
                    url: 'http://www.steinmart.com/product/exclusively+ours+-+floriana+12oz+scented+candle+58593237.do?sortby=priceAscend&refType=&from=fn',
                    image: 'https://ii.steinmart.com/fcgi-bin/iipsrv.fcgi?FIF=/images/steinmart/source/steinmart/products/58593237_979.tif&qlt=75&wid=270&cvt=jpeg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Mercury Glass Candle Votive & Baluster Stand';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '19.99',
                    url: 'http://www.steinmart.com/product/mercury+glass+candle+votive+%26+baluster+stand+58574526.do?sortby=priceAscend&refType=&from=fn',
                    image: 'https://ii.steinmart.com/fcgi-bin/iipsrv.fcgi?FIF=/images/steinmart/source/steinmart/products/58574526_600.tif&qlt=75&wid=270&cvt=jpeg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

