var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.sunandski.com/p/2515055341609/bogner-fire-amp-ice-women-s-sierra-ski-jacket"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Bogner Fire & Ice Women's Sierra Ski Jacket",
                    "price": "550.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://cdn-tp2.mozu.com/11961-16493/cms/16493/files/6d305237-de0f-4ebb-b259-bb3b9059c1cd?max=600',
                    "country": 'us',
                    "description": 'The Bogner Fire & Ice Womens Sierra Ski Jacket espouses a lively take on ski dressing, having orange, red, and navy color-blocks garnishing its white canvas as well as a detachable hood, various pockets, stretchy black cuffs with thumbholes, and tailored lines. Not only will you look good when on the mountain, but youll be ready for the elements while staying mobile, warm, and comfortable.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.sunandski.com/p/2150054238155/descente-men-s-stock-insulated-ski-pants"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Descente Men's Stock Insulated Ski Pants",
                    "price": "112.85",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://cdn-tp2.mozu.com/11961-16493/cms/16493/files/bbbdec9b-3880-405f-bc0f-6d03cdc3cd7a?max=600',
                    "country": 'us',
                    "description": "The Descente Men's Stock Insulated Ski Pants is constructed from a durable soft stretch fabric and comes equipped with a waist adjuster, leg ventilation, waterproof leg pocket and a snow cuff."
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
     describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.sunandski.com/c/womens-jackets?pageSize=192&"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Women's Jackets"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.sunandski.com/c/womens-jackets?pageSize=192&"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 192,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

     describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.sunandski.com/p/2515064581608/obermeyer-women-s-valerie-insulated-ski-jacket"
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.sunandski.com/c/womens-jackets?pageSize=192&"
        }

        // Change to be the number of products on the page
        var x = 192;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "Nils Women's Flo Insulated Ski Jacket Petite";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '445.00',
                    url: 'https://www.sunandski.com/p/2180061641600/nils-women-s-flo-insulated-ski-jacket-petite',
                    image: 'https://cdn-tp2.mozu.com/11961-16493/cms/16493/files/57bd1d88-dc09-441e-b69b-bcd33bff2c06?max=260&_mzcb=_1472997533234',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = "The North Face Women's Apex Elevation Jacket";
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '104.83',
                    url: 'https://www.sunandski.com/p/9273061707970/the-north-face-women-s-apex-elevation-jacket',
                    image: 'https://cdn-tp2.mozu.com/11961-16493/cms/16493/files/2332f405-e629-4f40-9d19-b8aedb9ea35d?max=260&_mzcb=_1472997533234',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
