var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('www.superdrug.com', function() {

    describe('In Stock Page', function() {

        var firstProductName = 'St. Ives Even & Bright Pink Lemon & Orange Scrub 150ml';
        it('first product should be ' + firstProductName, function (done) {

            var payload = {
                "pageType" : "product",
                "url" : "https://www.superdrug.com/Skin/Face-Skin-Care/Cleansers/Face-Scrubs/St-Ives-Even-%26-Bright-Pink-Lemon-%26-Orange-Scrub-150ml/p/738569",
                "html" : cache.load("https://www.superdrug.com/Skin/Face-Skin-Care/Cleansers/Face-Scrubs/St-Ives-Even-%26-Bright-Pink-Lemon-%26-Orange-Scrub-150ml/p/738569")
            }            

            Structure(payload, function (err, result) {

                should(err).be.equal(null)
                    result.name.should.be.equal(firstProductName)
                    result.price.should.be.equal("3.75")
                    result.url.should.be.equal("https://www.superdrug.com/Skin/Face-Skin-Care/Cleansers/Face-Scrubs/St-Ives-Even-%26-Bright-Pink-Lemon-%26-Orange-Scrub-150ml/p/738569") 
                    result.inStock.should.be.equal(true)
                    result.image.should.be.equal("https://www.superdrug.com/medias/sys_master/front-prd/front-prd/h29/h20/9103241248798/ST-IVES-FACE-SCRUB-EVEN-BRIGHT-150ML-738569.jpg")
                    result.country.should.be.equal("uk")
                    done()
                });
            })

    

        

              

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.superdrug.com/Toiletries/Dental/c/toil-dental",
            "html" : cache.load("https://www.superdrug.com/Toiletries/Dental/c/toil-dental")
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Oral B Pro 600 Pink Special Edition Electric Toothbrush';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '49.99',
                    url: 'https://www.superdrug.com/Oral-B/Oral-B-Pro-600-Pink-Special-Edition-Electric-Toothbrush/p/284715',
                    image: 'https://www.superdrug.com/medias/sys_master/front-src/front-src/hc8/h77/9291569856542/Oral-B-Pro-600-Pink-Special-Edition-Electric-Toothbrush-284715.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Oral B Cross Action Replacement Electric Toothbrush Heads x4';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '18.99',
                    url: 'https://www.superdrug.com/Oral-B/Oral-B-Cross-Action-Replacement-Electric-Toothbrush-Heads-x4/p/982174',
                    image: 'https://www.superdrug.com/medias/sys_master/front-src/front-src/hd6/h27/9291495473182/Oral-B-Cross-Action-Replacement-Electric-Toothbrush-Heads-x4-982174.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.superdrug.com/Baby/Baby%27s-Nursery/c/babys-nursery",
            "html" : cache.load("http://www.superdrug.com/Baby/Baby%27s-Nursery/c/babys-nursery")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 20,
                    "pages": 4,
                    "url" : "http://www.superdrug.com/Baby/Baby%27s-Nursery/c/babys-nursery"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.superdrug.com/Baby/Baby%27s-Nursery/c/babys-nursery",
            "html" : cache.load("http://www.superdrug.com/Baby/Baby%27s-Nursery/c/babys-nursery")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Baby's Nursery",
                    "url" : "http://www.superdrug.com/Baby/Baby%27s-Nursery/c/babys-nursery"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
