    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.surfdome.com/Womens_Boots-428/"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Womens Boots"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.surfdome.com/Womens_Boots-428/"
        }

        // Change to be the number of products on the page
        var x = 157;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Joules Printed Welly Wellington Boots - Navy Spots';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '33.99',
                    url: 'https://www.surfdome.com/joules_wellington_boots_-_joules_printed_welly_wellington_boots_-_navy_spots-227888',
                    image: 'https://asset1.surfcdn.com/joules-wellington-boots-joules-printed-welly-wellington-boots-navy-spots.jpg?w=300&h=300&r=4&q=80&o=f@4qjYV3X2JlWXHjBC8jiA3R@EQj&V=xxWM',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Hunter Welly Socks - Dark Ruby';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '5.39',
                    url: 'https://www.surfdome.com/hunter_socks_-_hunter_welly_socks_-_dark_ruby-154946',
                    image: 'https://asset3.surfcdn.com/hunter-socks-hunter-welly-socks-dark-ruby.jpg?w=300&h=300&r=4&q=80&o=wvsZYruPKOjbsMkGI2ZpK@DjLo4j&V=2JAp',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

