var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.sweatband.com', function() {

    before(function()  {
        this.timeout(5000);
    })

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.sweatband.com/powerbilt-countess-package-set.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "PowerBilt Countess Package Set",
                    "price": "224.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://cdn.sweatband.com/PowerBilt Countess All Graphite Package Set_PowerBilt_Countess_All_Graphite_Package_Set_400x400.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.sweatband.com/lomography-konstruktor-diy-super-kit-camera.html?utm_source=awin&amp;utm_campaign=ss&amp;utm_medium=cpc&amp;utm_term=Lomography+Konstruktor+DIY+Super+Kit+Camera"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Lomography Konstruktor DIY Super Kit Camera",
                    "price": "34.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://cdn.sweatband.com/lomography_konstruktor_diy_super_kit_camera_lomography_konstruktor_diy_super_kit_camera1_400x400.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.sweatband.com/boxing-gloves.html#p=0"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 15,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.sweatband.com/boxing-gloves.html#p=0"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Boxing Gloves"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.sweatband.com/squash-bags.html"
        }

        // Change to be the number of products on the page
        var x = 15;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Head Extreme Monstercombi 12 Racket Bag';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '50.90',
                    url: 'https://www.sweatband.com/head-extreme-monstercombi-12-racket-bag.html',
                    image: 'https://cdn.sweatband.com/head_extreme_monstercombi_12_racket_bag_ss17_head_extreme_monstercombi_12_racket_bag_ss17_245x245.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Yonex 9626B Pro Badminton 6 Racket Bag' ;
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '50.98',
                    url: 'https://www.sweatband.com/yonex-9626b-pro-badminton-6-racket-bag.html',
                    image: 'https://cdn.sweatband.com/yonex_9626b_pro_badminton_6_racket_bag_yonex_9626b_pro_badminton_6_racket_bag_245x245.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
